import React, { useState } from 'react';
import { useStateValue } from '../StateProvider';
import './Money.css';
import {Link} from 'react-router-dom'

function Money() {
    const[{user},dispatch]=useStateValue();
    const[person,setPerson]=useState('');
    const[amount,setAmount]=useState(0);


    console.log(person);
    console.log(amout)
    const sendMoney=()=>{
        dispatch({
            type: "SELECTED_USER",
            amt:amount,
            name:person
        })

    }
    

    let userOption=user.map((item)=>{
        return(
            <option value={item.id}>{item.first}</option>
        )
    })
    return (
        <div className="money">
            <h1 style={{fontWeight:"700"}}>Transfer Portal</h1>
            <div className="money__container">
                
                <form className="money__form">
                    <div style={{display:"flex",justifyContent:"center",alignItems:"center",flexDirection:"column"}}>
                        <h4>Whom do you want to transfer the money ?</h4>
                        <select onChange={e=>setPerson(e.target.value)}>
                            {userOption}
                        </select>
                    </div>
                    <div style={{display:"flex",justifyContent:"center",alignItems:"center",flexDirection:"column"}}>
                        <h4>Enter the amount</h4>
                        <input onChange={e=>setAmount(e.target.value)} type="text"></input>
                    </div>
                    <Link to="/viewCustomer"> 
                        <button onClick={sendMoney}>SEND</button>
                    </Link>

                </form>

                
            </div>
        </div>
    )
}

export default Money
