import React from 'react';
import {Link} from 'react-router-dom';
import './CustomerInfo.css';
import {useStateValue} from '../StateProvider';

function CustomerInfo({first,last,balance,mail,id}) {
    const[{userIndex},dispatch]=useStateValue();
    const handleEvent=(e)=>{
        e.pre
       dispatch({
           type:"SET_INDEX",
           index:id
       })
    }

    return (
       
             <tr> 
                    <td>{first}</td>
                    <td>{last}</td>
                    <td>{balance}</td>
                    <td>{mail}</td>
                    <td><Link to="/transfer/"><input onClick={handleEvent()}  value="Transfer" type="submit" style={{backgroundColor:"rgb(9, 58, 9)",color:"white",width:"100%",height:"40px"}}/></Link></td>
                </tr>
     
    )
}

export default CustomerInfo
