import axios from 'axios';
import { useStateValue } from './StateProvider';
const [{user},dispatch]=useStateValue();

export const initialState={
    user:null
    ,currIndex:0,
    currUser:null,
    sellUser:null,
    amount:0
};



const reducer= (state,action)=>{

    switch(action.type){
       case "SET_INDEX":
           return{
               ...state,
               currIndex:action.index
           };
        case "SET_USER":
            return {
                ...state,
                currUser:action.user

            };
        case "SELECTED_USER":
            const userInd=state.user.findIndex(item=>item.first==action.name);

            // const newUser=[...state.user];
            // newUser[userInd]=newUser[userInd].balance>action.amt?{...newUser[userInd],balance:Number(newUser[userInd].balance) + Number(action.amt)}:null

            const userIndex=state.user.findIndex(item=>item.first==state.currUser.first);
            // newUser[userIndex]=newUser[userIndex].balance>action.amt?{...newUser[userIndex],balance:newUser[userIndex].balance-action.amt}:null
            async () => {
                const result=await  axios({
                     url:'http://localhost:8080/api/send',
                     method:"POST",
                     payload:{
                         id:userInd,
                         amount:action.amt
                     }
                   
                   })
                   console.log(result.data)
                   
                   
               }
               const newUser=[...state.user,state.user[userInd]]
            return{
                ...state,
                user:newUser
                
            };
        case "TAKE_USER":
            return{
                ...state,
                user:action.user
            };
                
            

           

    }
};

export default reducer;
