import React from 'react';
import './Customer.css';
import {Link} from 'react-router-dom';
import { useStateValue } from '../StateProvider';


function Customer() {
    const[{user},dispatch]=useStateValue(); 


    const CustomerInfo=({first,last,balance,mail})=>{
        return(
            <div>
                    <tr>
                    <td>{first}</td>
                    <td>{last}</td>
                    <td>{balance}</td>
                    <td>{mail}</td>
                    <td><Link><button style={{backgroundColor:"rgb(9, 58, 9)"}}>Transfer</button></Link></td>
                </tr>
            </div> 
        )
    } 
    
    console.log({user});
    return (
        <div  className="customer">
            <div className="customer__container">
                <h1>CUSTOMERS</h1>
            <table className="table">
                <thead className="head">
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Balance</th>
                    <th>Email</th>
                    <th></th>
                </thead>
                <tbody>
                    {user.map((item)=>{
                      <CustomerInfo first={item.first} last={item.last} balance={item.balance} mail={item.mail} />
                    })}
                    
                </tbody>
               
                </table>
                <Link to="/">
                    <button style={{width:"20%"}}>Back</button> 
                </Link>

       

                

            </div>
        </div>
    )
}

export default Customer
